package Third_code;

public class Coding {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		while (a >= 0) {
			System.out.println("This is number "+a);
			a--; 
		} 
	
		System.out.println("\n");
		
		int d = 0;
		do {
			System.out.println("This is number d: "+d);
			d++;
		}while(d <= 20); 
	}
	
	

}
