package Third_code;

public class Grt_coding {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 60;
		int s = 2;
		int k = 6;
		
		if(i > s) {
			if(i > k) {
				System.out.println("This is the number i "+i);
			}else{
				System.out.println("This is the number k "+k);
			}
		}else if(k > s) {
			System.out.println("This is number k "+k);
		}else {
			System.out.println("this is number s "+s);
		}
	}

}
